import React, {useState, useRef, useEffect } from 'react';
import { 
    View, 
    Text, 
    StyleSheet,
    Button,
    Alert 
} from 'react-native';
import NumberContainer from '../components/NumberContainer';
import Card from '../components/Card';

const generateRandomBetween = (min,max,exclude) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    console.log(min, max);
    const rndNum = Math.floor(Math.random() * (max - min) ) + min;
    if (rndNum === exclude) {
        return generateRandomBetween(min,max,exclude);
    } else {
        return rndNum;
    }
};
const Gamescreen = props => {
    // setting the default value by generating the number in useState(generateRandomBetween)
    // This will just initialize the value
    const [currentGuess, setCurrentGuess] = useState(() => {
         return generateRandomBetween(1,100,props.userChoice)}
    );
    const [rounds, setRounds] = useState(0);
    const currentLow = useRef(1);
    const currentHigh = useRef(100);
    
    const {userChoise, onGameOver} = props;
      
    // useEffect hook run logic AFTER the render cycle for this component
    useEffect(() => {
        if (currentGuess === props.userChoise) {
           props.onGameOver(rounds);
        }
    }, [currentGuess, userChoise, onGameOver])


    const nextGuessHandler = direction => {
        if ((direction === 'lower' && currentGuess < props.userChoice) || (direction === 'higher' && currentGuess > props.userChoice)) {
            Alert.alert('Dont\'t lie', 'You know that this is wrong', [
                {text: 'Sorry!', style: 'cancel'}
            ]);
            return;
        }
        if (direction === 'lower') {
            currentHigh.current = currentGuess; 
        } else {
            currentLow.current = currentGuess; 
        }
        
        const nextNumber = generateRandomBetween(currentLow.current,currentHigh.current,currentGuess);
        console.log(nextNumber);
        setCurrentGuess(nextNumber); 
        setRounds(curRounds => curRounds + 1); 
    };

    return (
        <View style={styles.screen}>
            <Text>Opponent's Guess</Text>
            <NumberContainer>{currentGuess}</NumberContainer>
            <Card style={styles.buttonContainer}> 
                <Button title="LOWER" onPress={nextGuessHandler.bind(this,'lower' )} />
                <Button title="GREATER" onPress={nextGuessHandler.bind(this,'higher' )} />
            </Card>
        </View>
    );


};

const styles = StyleSheet.create({
    screen: {
        flex: 1,
        padding: 10,
        alignItems: 'center'
    },
    buttonContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        marginTop: 20,
        width: 300,
        maxWidth: '80%'
        
    }

});

export default Gamescreen;
